root.script:
  	function setPlayerColor(){
	  	var data = $.GetContextPanel().Data;
      if (data == null) { $.Schedule(1, setPlayerColor); return;}
	  	var playerid = data['PlayerID'];
	  	var playercolor = Players.GetPlayerColor(playerid).toString(16).substring(2,8);

      var localPlayer = Players.GetLocalPlayer();
      if (localPlayer == playerid) {
        $.GetContextPanel().style.backgroundColor = '#' + playercolor + "44;";
        $.GetContextPanel().style.border = "2px solid #" + playercolor + ";"
      } else {
        $.GetContextPanel().style.backgroundColor = "#66f6de11;";
        $.GetContextPanel().style.border = "3px solid #f6de11;"      
      }

      $("#overhead_name").steamid = Game.GetPlayerInfo(playerid).player_steamid;

	  	$.GetContextPanel().style.backgroundColor = '#' + playercolor + "55;";
	  	$.GetContextPanel().style.border = "2px solid #" + playercolor + ";"
	  	$.Schedule(1, setPlayerColor);
  	}

  	(function() {
  		setPlayerColor();
  	})();
